import classNames from "classnames/bind";
import styles from "./Menu.module.scss";
const cx = classNames.bind(styles);

function MenuItem({ data, title }) {
  const handleChange = (event) => {
    const selectedOption = event.target.selectedOptions[0];
    const selectedUrl = selectedOption.getAttribute("value");
    window.location.href = selectedUrl;
  };

  return (
    <select className={cx("menu-item")} onChange={handleChange}>
      <option disabled selected hidden className = {cx("value")}>{title}</option>
      {data.map((item, index) => (
        <option  className = {cx("value")} key={index} value={item.to}>{item.value}</option>
      ))}
    </select>
  );    
}

export default MenuItem;
