import { toast } from "react-toastify";

const Notification = ({ content, statuss, next = null }) => {

    toast[statuss](content);

    if (next !== null) {
        setTimeout(() => {
            window.location.replace("/login");
        }, 1000);
    }

    return null; // Return null or an empty element because this component doesn't render anything
};

export default Notification;
