import classNames from "classnames/bind";

import styles from "./Create.module.scss";
import Input from "../../../../components/Input";
import Button from "../../../../components/Button";
import { useEffect, useState } from "react";

const cx = classNames.bind(styles)

function MoreSkills({title, value}) {

    const [data, setData] = useState([]);
    const [Name, setName] = useState("");
    const [Percentage, setPercentage] = useState("");

    useEffect(() => {
        if(value.length > 0){
            setData(value)
        }elseif(value.length < 0)
    }, [])

    const handleUpData = () => {
        setData([...data,{"Name": Name, "Percentage": Percentage}]);
        setName("");
        setPercentage("");
    }

    const handleDelete = (id) => {
        const newData = data.filter((item, index) => index !== id);
        setData(newData);
        console.log(data);
    }

    return (
        <section className={cx("warpper")}>
            <section className={cx("container")}>
                <section className={cx("input_item")}>
                    <Input
                        name="Name Skills"
                        id="Name"
                        required={true}
                        type="text"
                        value={Name}
                        onChange={(e) => {setName(e.target.value)}}
                    />
                    <Input
                        name="Percentage"
                        id="Percentage"
                        required={true}
                        type="number"
                        max="100"
                        min="0"
                        value={Percentage}
                        onChange={(e) => {setPercentage(e.target.value)}}
                    />
                    <Button
                        name="Create Skills"
                        width= "25%"
                        height= "45px"
                        onClick={() => {handleUpData()}}
                    />
                </section>
                <section className={cx("list-skills")}>
                    <table className={cx("table")}>
                        <thead>
                            <tr>
                                <th>Name Skills</th>
                                <th>Percentage</th>
                                <th>Action</th>
                            </tr>
                        </thead>
                        {
                            data.length >0 ?
                            <tbody>
                            {
                                data.map((item, index) => (
                                    <tr key={index}>
                                        <td> {item.Name}</td>
                                        <td> {item.Percentage}</td>
                                        <td><button onClick={() => {handleDelete(index)}} className={cx("Delete")}>Delete</button></td>
                                    </tr>
                                ))
                            }
                            </tbody>
                        : ""
                        }
                    </table>
                </section>
            </section>
        </section>
    )
}

export default MoreSkills;
