import className from "classnames/bind";
import { useState } from "react";
import axios from "axios";

import styles from "./Login.module.scss";
import { images } from "../../asset/img";
import Input from "../../components/components/Input";
import Button from "../../components/components/Button";

const cx = className.bind(styles);

function Login() {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    const handleLogin = async (e) => {
        e.preventDefault(); // Ngăn chặn hành vi gửi mặc định của form

        try {
            const response = await axios.post("http://localhost:7000/api/account/signin", {
                email: email,
                password: password
            });
            console.log(response);
            // Thêm logic xử lý đăng nhập thành công (ví dụ: chuyển hướng đến trang khác)
        } catch (error) {
            console.log(error);
            // Thêm logic xử lý đăng nhập thất bại (ví dụ: hiển thị thông báo lỗi)
        }
    };

    const handleInputChange = (e, inputType) => {
        const value = e.target.value;
        if (inputType === "email") {
            setEmail(value);
        } else if (inputType === "password") {
            setPassword(value);
        }
    };

    return ( 
        <main>
            <section className={cx("container")}>
                <article className = {cx("images")}>
                    <img src={images.Lovlyze} alt="lovlyze"/>
                </article>
                <section  className={cx("content")}>
                    <form action="/login" method="post" onSubmit={handleLogin} >
                        <Input 
                            name="Gmail"
                            type="email"
                            required = "true"
                            title= "gmail cannot be empty"
                            id="email"
                            onChange={(e) => handleInputChange(e, "email")}
                        />
                        <Input 
                            name="Password"
                            type="password"
                            required = "true"
                            title= "password cannot be empty"
                            id="password"
                            onChange={(e) => handleInputChange(e, "password")}
                        />
                        <Button
                            name="Login"
                            type= "submit"
                            width = "30%"
                            height= "15%"
                            margin= "4% 35%"
                        />
                    </form>
                 </section>
            </section>
        </main>
     );
} 

export default Login;
