import * as htttpRequest from "../utils/httpRequest";

export const sendMessage = async (body) => {
    
    try {
        const response = await htttpRequest.post(`message/sendmessage`, {
            ...body
        })
        return response;
    } catch (error) {
        console.log(error);
    }
}

export const getMessage = async () => {
    try {
        const response = await htttpRequest.get(`message/getmessage`, {});
        return response;
    } catch (error) {
        console.log(error);
    }
}

export const updateStatusMessage = async (id)
