import classNames from "classnames/bind";

import styles from './Option.module.scss';

const cx = classNames.bind(styles);

function Option({name, required = false,data}) {
    return (
        <section className={cx("warpper")}>
            <p  className={cx('title')}>{name}</p>
            <select className={cx("select")} required ={required}>
                <option selected>Open {name}....</option>
                {
                    data.map((item, key) => (
                         <option key={key} value={key}>{item}</option>                       
                    ))
                }
            </select>
        </section>
    )
}

export default Option;
