import classNames from "classnames/bind";
import { useState } from "react";

import styles from "./Home.module.scss";
import Input from "../../../components/Input";
import Button from "../../../components/Button";
import { updateStatusMessage } from "../../../Services/sendMessage";


const cx = classNames.bind(styles);

function Message({item}) {

    const [display, setDisplay] = useState(false);
    const [color, setColor] = useState("#fdfeff");

    const handleDisplay = () => {
        setDisplay(!display);
        if (item.Status === 0) {
            setColor(""); // Xóa màu khi click
        }        
    };

    const handleLogId = (id) => {
        try {
            updateStatusMessage(id);
        } catch (error) {
            console.log(error);
        }
    };

    return ( 
        <section className={cx("message")} key={item._id} onClick={() => handleLogId(item._id)}>
            <article className={cx("title")} onClick={handleDisplay} 
                style={{color: item.Status === 0  && status === true ? "#fdfeff" : ""}}>
                <p className={cx("nameTitle")}>Khanh Dinh Van</p>
                <h1 className={cx("titleMessage")}>{item.TitleMessage}</h1>
                <i className={cx("bi bi-trash3", "remote")}></i>
            </article>
            <section
                className={cx("container")}
                style={{ display: display ? "block" : "none" }}
            >
                <article className={cx("itemTilteMessage")}>
                    <h1>{item.TitleMessage}</h1>
                </article>
                <article className={cx("itemAccountReceiver")}>
                    <span>{item.NameUserReceiver}</span>
                    <p>{item.EmailReceiver}</p>
                </article>
                <article className={cx("itemContentMessage")}>
                    <p>{item.Content}</p>
                </article>
                <form className={cx("itemReplyMessage")}>
                    <Input
                        name="Reply The Message"
                        type="text"
                        required
                        id={`itemReplyMessage-${item._id}`}
                        onFocus={() => {}}
                    />
                    <Button
                        name="Send Message"
                        width="15%"
                        height="10%"
                        padding="1%"
                    />
                </form>
            </section>
        </section>        
    );
}

export default Message;
