import React, { useState, useRef } from "react";
import classNames from "classnames/bind";

import styles from "./PDFViewer.module.scss";

const cx = classNames.bind(styles);

function PDFViewer({ name, path }) {
  const [File, setFile] = useState(null);
  const [ViewFile, SetViewFile] = useState(null)
  // const [pageNumber, setPageNumber] = useState(1);
  const fileInputRef = useRef(null);

  // const onDocumentLoadSuccess = ({ numPages }) => {
  //   setNumPages(numPages);
  // };

    // Hàm xử lý khi nút được nhấn
    const handleButtonClick = () => {
        // Kích hoạt sự kiện click trên thẻ input
        fileInputRef.current.click();
    };

    const fileType = ["application/pdf"];
    const handleFileInputChange = (e) => {
      let selecteFile = e.target.files[0]
      if(selecteFile){
        if(selecteFile && fileType.includes(selecteFile.type)){
          let render = new FileReader();
          render.readAsDataURL(selecteFile);
          render.onload = (e) => {
            SetViewFile(e.target.result)
          }
        }
      }
    }
  

  return (
    <section className={cx("wrapper")}>
      <article className={cx("show-file")}>
        
      </article>
      <article className={cx("input-file")}>
        <button onClick={handleButtonClick}>
          Chọn {name}
          {/* Thêm ref vào thẻ input để tham chiếu đến nó */}
          <input
              ref={fileInputRef}
              type="file"
              name={name}
              onChange={(e) => handleFileInputChange(e)}
          />
      </button>
      </article>
    </section>
  );
}

export default PDFViewer;
