import classNames from "classnames/bind";
import 'bootstrap-icons/font/bootstrap-icons.css';
import { useEffect ,useState } from "react";

import styles from "./Notification.module.scss"

const cx = classNames.bind(styles);

function Notification({ content, title, type }) {


  const [icon, setIcon] = useState();

  switch (type) {
    case 'error':
      setIcon(<i style={{ color: "red" }} className="bi bi-x-circle"></i>);
      break;
    case 'warning':
      setIcon(<i style={{ color: "yellow" }} className="bi bi-exclamation-circle"></i>);
      break;
    default:
      setIcon(<i style={{ color: "green" }} className="bi bi-check2-circle"></i>);
  }
console.log(icon)
  return (
    <section className={cx("container")}>
      <section className={cx('header')}>
        <article className={cx('title')}>
          {icon}
          <h1>{title}</h1>
        </article>
        <i className={cx("bi bi-x close")}></i>
      </section>
      <article className={cx("content")}>
        {content}
      </article>
    </section>
  );
}

export default Notification;
