import classNames from "classnames/bind";
import { useState } from "react";

import styles from "./Home.module.scss";
import Input from "../../../components/Input";
import Button from "../../../components/Button";
import Notification from "../../../components/Notification";
import { getMessage } from "../../../Services/sendMessage";


const cx  = classNames.bind(styles);
function Home() {
    
    const [display, setDisplay] = useState(false);
    const [message, setMessage] = useState(null);
    const [data, setData] = useState(null);
    const [id, setId] = useState("");
    const [content, setContent] = useState("");
    const [emailReceiver, setEmailReceiver] = useState("");
    const [nameUserReceiver, setNameUserReceiver] = useState("");
    const [TitleMessage, setTitleMessage] = useState("");

    const handleDisplay = () => {
        setDisplay(!display);
    }

    // call api
    const fetchAPI = async () => {
        try {
            const result = await getMessage();
            setData(result.dataMessage);
            //  for (const item of ) {
                
            // }
        } catch (error) {
            console.log(error);
            setMessage("The system is maintenance");
        }
    }
fetchAPI ();
    return ( 
        <>
        (
            for (const item  of data) {
                <section className={cx("message")}>
                    <article className={cx("title")} onClick={handleDisplay} >
                        <p className={cx("nameTitle")}>Khanh Dinh Van</p>
                        <h1 className={cx("titleMessage")}>
                            item.TitleMessage
                        </h1>
                        <i className={cx("bi bi-trash3", "remote")}></i>
                    </article>
                    <section className={cx("container")} style={{display: display ? "block" : "none"}}>
                        <article className={cx("itemTilteMessage")}>
                            <h1 >item.TitleMessage</h1>
                        </article>
                        <article className={cx("itemAccountReceiver")}>
                            <span >item.NameUserReceiver</span>
                            <p>item.EmailReceiver</p>
                        </article>
                        <article className={cx("itemContentMessage")}>
                            <p>item.Content</p>
                        </article>
                        <form  className={cx("itemReplyMessage")}>
                            <Input
                                name="Reply The Message"
                                type= "text"
                                required
                                id="itemReplyMessage"
                                onFocus
                            />
                            <Button
                                name="Send Message"
                                width = "15%"
                                height = "10%"
                                padding = "1%"
                            />
                        </form>
                    </section>
                </section>

            }
        )
        </>
     );
}

export default Home;
