import { toast } from "react-toastify";
import { useNavigate } from "react-router-dom";

const Notification = ({ content, statuss, next = null }) => {
    const history = useNavigate();

    toast[statuss](content);

    if (next !== null) {
        setTimeout(() => {
            history.push(next);
        }, 1000);
    }

    return null; // Return null or an empty element because this component doesn't render anything
};

export default Notification;
