import { toast } from "react-toastify";
import { useHistory } from "react-router-dom";

export const notification = (content, statuss, next = null) => {
    const history = useHistory();

    toast[statuss](content);

    if (next !== null) {
        setTimeout(() => {
            history.push(next);
        }, 1000);
    }
};
