import classNames from "classnames/bind";
import styles from './Option.module.scss';

const cx = classNames.bind(styles);

const handleChange = (e) => {
    console.log(e);
}

function Option({ name, data }) {
    return (
        <section className={cx("warpper")}>
            <p className={cx('title')}>{name}</p>
            <select className={cx("select")} onChange={(e) => handleChange(e)}>
                <option disabled selected>Open {name}....</option>
                {data.map((item, index) => {
                    const key = Object.keys(item)[0]; // Lấy key từ đối tượng
                    const value = item[key]; // Lấy giá trị từ đối tượng
                    return (
                        <option key={index} value={key}>
                            {value}
                        </option>
                    );
                })}
            </select>
        </section>
    )
}

export default Option;
