import classNames from "classnames/bind";
import { useState } from "react";

import styles from "./CreateAccount.module.scss";
import Input from "../../../components/Input";
import Button from "../../../components/Button";
import Product from "./MoreProduct";
import { postAccount, cancenAPIAccount } from "../../../Services/account";
import Notification from "../../../components/Notification";
import axios from "axios";

const cx = classNames.bind(styles);

function Account() {
    const [Name, setName] = useState("");
    const [Email, setEmail] = useState("");
    const [Birthday, setBirthday] = useState("");
    const [From, setFrom] = useState("");
    const [Password, setPassword] = useState("");
    const [Majors, setMajors] = useState("");
    const [Maxim, setMaxim] = useState("");
    const [Describe, setDescribe] = useState("");
    const [CV, setCV] = useState("");
    const [Image, setImages] = useState("");
    const [Logo, setLogo] = useState("");
    const [IconLogo, setIconLogo] = useState("");
    const [Job, setJob] = useState([]);
    const [Phone, setPhone] = useState([]);
    const [Language, setLanguage] = useState([]);

    const handleJob = (data) => {
        setJob(data);
    };
    const handlePhone = (data) => {
        setPhone(data);
    };
    const handleLanguage = (data) => {
        setLanguage(data);
    };

    const handleUpAccount = async () => {
        try {
            const formData = new FormData();
            formData.append("Images", Image);
            formData.append("CV", CV);
            formData.append("Logo", Logo);
            formData.append("IconLogo", IconLogo);
            formData.append("Name", Name);
            formData.append("Email", Email);
            formData.append("Birthday", Birthday);
            formData.append("From", From);
            formData.append("Password", Password);
            formData.append("Majors", Majors);
            formData.append("Maxim", Maxim);
            formData.append("Describe", Describe);

            // Chuyển các mảng Phone và Language thành chuỗi trước khi thêm vào FormData
            formData.append("Phone", Phone.join(","));
            formData.append("Language", Language.join(","));

            // Chuyển mảng Job thành chuỗi JSON trước khi thêm vào FormData
            formData.append("Job", JSON.stringify(Job));

            const result = await axios.post("http://localhost:7000/api/account/createAccount", formData, {
                headers: {
                    "Content-Type": "multipart/form-data",
                },
            });
            console.log(result);
            if (result.data.message === "Create account success") {

                // Xóa giá trị của các trường sau khi tạo tài khoản thành công
                setName("");
                setEmail("");
                setBirthday("");
                setFrom("");
                setPassword("");
                setMajors("");
                setMaxim("");
                setDescribe("");
                setCV("");
                setImages("");
                setLogo("");
                setIconLogo("");
                setJob([]);
                setPhone([]);
                setLanguage([]);
            }
        } catch (error) {
            console.log(error);
        }
    };

    return (
        <section className={cx("Container")}>
            <article className={cx("itemLeft")}>
                {/* Các Input components */}
            </article>
            <article className={cx("itemRight")}>
                <Product value={Job} name="Job" type="text" onDataUpdate={handleJob} />
                <Product value={Language} name="Language" type="text" onDataUpdate={handleLanguage} />
                <Product value={Phone} name="Phone" type="number" onDataUpdate={handlePhone} />
                {/* Các Input components */}
                <Button name="Create Account" width="30%" height="7%" onClick={handleUpAccount} />
            </article>
        </section>
    );
}

export default Account;
