import classNames from "classnames/bind";
import { useRef, useEffect } from "react";

import styles from "./Input.module.scss";

const cx = classNames.bind(styles);

function Input({ name, type, required = false, title, id, value, onChange, onFocus }) {
  const inputRef = useRef(null);

  useEffect(() => {
    if (inputRef.current) {
      // Focus on the input when it's available
      inputRef.current.focus();
    }
  }, []);
  return (
    <>
      <label htmlFor={id} className={cx('label')}>
        {name}
      </label>
      <input 
        type={type} 
        required={required} 
        title={title} 
        name={id} 
        id={id} 
        className={cx('input')} 
        value={value}
        onChange={onChange}
        />
    </>
  );
}

export default Input;
