    import { useRef, useState, useEffect } from "react";
    import classNames from "classnames/bind";
    import style from "./UploadImage.module.scss";

    const cx = classNames.bind(style);

    function UploadImage({ Item, name, id, required = false, multiple = false }) {

        // Sử dụng useRef để tham chiếu đến thẻ input
        const fileInputRef = useRef(null);
        const [image, setImages] = useState();

        // Sử dụng useEffect để cập nhật ảnh khi nhận được một danh sách ảnh mới từ bên ngoài
        useEffect(() => {
            if (item) {
                setImages(Array.isArray(item) ? item : [item]);
            }
        }, [item]);
        // Hàm xử lý khi có sự thay đổi trên thẻ input (đã chọn file)
        const handleFileInputChange = () => {
            const selectedFiles = fileInputRef.current.files;
            const newImages = Array.from(selectedFiles).map(file => ({
                file: file,
                preview: URL.createObjectURL(file)
            }));
            setImages([...images, ...newImages]);
        };

        // Hàm xử lý khi xóa một ảnh từ danh sách
        const handleRemoveImage = (image) => {
            URL.revokeObjectURL(image.preview);
            setImages(images.filter((img) => img !== image));
        };

        return (
            <section className={cx("wrapper")}>
                <section className={cx("content")}>
                    <article className={cx("images")}>
                        {image && (
                            <img src={image.preview} alt="Image" />
                        )}

                    </article>
                    <article className={cx("item")}>
                        <button onClick={handleButtonClick}>
                            Chọn {name}
                            {/* Thêm ref vào thẻ input để tham chiếu đến nó */}
                            <input
                                ref={fileInputRef}
                                type="file"
                                name={name}
                                id={id}
                                required={required}
                                multiple={multiple}
                                onChange={handleFileInputChange}
                            />
                        </button>
                    </article>
                </section>
            </section>
        );
    }

    export default UploadImage;
