import classNames from "classnames/bind";
import styles from "./List.module.scss";
import Input from "../../../../components/Input";
import MoreSkills from "../Create/MoreSkills";
import { useEffect, useState } from "react";
import { deleteSkill, getSkill } from "../../../../Services/skills";
import { toast } from "react-toastify";
import Button from "../../../../components/Button";

const cx = classNames.bind(styles);

function ListSkills() {
    
    const [data, setData] = useState([]);
    const [Skills, SetSkills] = useState([]);
    const [TitleSkills, SetTitleSkills] = useState("");
    const [ContentSkills, SetContentSkills] = useState("");

    const getAllSkills = async () => {
        try {
            const result = await getSkill();
            setData(result.data);
        } catch (error) {
            toast.error("Can not data")
        }
    }

    useEffect(() => {
        getAllSkills()
    }, []);

    const handleDelete = async (id) => {
        try {
            const resutlt = await deleteSkill(id);
            toast.success(resutlt.message);
            getAllSkills();
        } catch (error) {
            console.log(error);
        }
    } 
    const handleUpdate = async (id) => {
        console.log("ContentSkills: ", ContentSkills);
        console.log("TitleSkills: ", TitleSkills);
        console.log("Skills: ", Skills);
    } 

    return (
        <section className={cx("warpper")}>
        {
            data && data.map((item, key) => (
                <section key={key} className={cx("skills")}>
                    <Input
                        name="Title Skills"
                        id="TitleSkills"
                        value={item.TitleSkills}
                        onChange={() => {TitleSkills()}}
                    />
                    <Input
                        name="Content Skills"
                        id="ContentSkills"
                        value={item.ContentSkills}
                        onChange={() => {ContentSkills()}}
                    />
                    <MoreSkills
                        dataOnMore={SetSkills}
                        value={item.Skills}
                    />
                    <Button
                        name="Delete"
                        width="100px"
                        height = "35px"
                        margin = "20px"
                        onClick={() => {handleDelete(item._id)}}
                    />
                    <Button
                        name="Update"
                        width="100px"
                        height = "35px"
                        margin = "20px"
                        onClick={(e) => {handleUpdate(item._id)}}
                    />
                </section>
            ))
        }
        </section>
    )
}
 export default ListSkills;
