import classNames from "classnames/bind";
import { useState} from "react";

import styles from "./CreateAccount.module.scss";
import Input from "../../../components/Input";
import Button from  "../../../components/Button";
import Product from "./MoreProduct";
import { postAccount,  cancenAPIAccount } from "../../../Services/account";
import Notification from "../../../components/Notification";
import axios from "axios";

const cx =classNames.bind(styles);

function Account() {

    const [Name, setName] = useState("");
    const [Email, setEmail] = useState("");
    const [Birthday, setBirthday] = useState("");
    const [From, setFrom] = useState("");
    const [Password, setPassword] = useState("");
    const [Majors, setMajors] = useState("");
    const [Maxim, setMaxim] = useState("");
    const [Describe, setDescribe] = useState("");
    const [CV, setCV] = useState("");
    const [Image, setImages] = useState("");
    const [Logo, setLogo] = useState("");
    const [IconLogo, setIconLogo] = useState("");
    const [Job, setJob] = useState("");
    const [Phone, setPhone] = useState("");
    const [Language, setLanguage] = useState("");


    // thông báo lỗi
    const [error, setError] = useState("");
    const [alert, setAlert] = useState("");

    const [notificationKey, setNotificationKey] = useState(0);

    const handleJob = (data) => {
        setJob(data);
    };
    const handlePhone = (data) => {
        setPhone(data);
    };
    const handleLanguage = (data) => {
        setLanguage(data);
    };
    
    const handleUpAccount = async (e) => {
        
        try {
            const formData  = new FormData();
            formData.append("Images", Image);
            formData.append("CV", CV);
            formData.append("Logo", Logo);
            formData.append("IconLogo", IconLogo);
            formData.append("Name", Name);
            formData.append("Email", Email);
            formData.append("Birthday", Birthday);
            formData.append("Phone", Phone);
            formData.append("From", From);
            formData.append("Language", Language);
            formData.append("Password", Password);
            formData.append("Majors", Majors);
            formData.append("Maxim", Maxim);
            formData.append("Describe", Describe);
            formData.append("Job", Job);

            // const retultformData  = await postAccount(formData );
            const retultformData  = await postAccount(formData );
            console.log(retultformData );
            if(retultformData.message === "Sign up success"){
                setAlert(retultformData.message);
                setError("");
                
                setName("");
                setEmail("");
                setBirthday("");
                setFrom("");
                setPassword("");
                setMajors("");
                setMaxim("");
                setDescribe("");
                setCV("");
                setImages("");
                setLogo("");
                setIconLogo("");
                setJob("");
                setPhone("");
                setLanguage("");
               }
                   
        } catch (error) {
            console.log(error);
        }


    }

    return ( 
        <section className={cx("Container")}>
<form onSubmit={handleUpAccount}>
                <article className={cx("itemLeft")}>
                    <Input 
                        name="Name"
                        type="text"
                        required
                        title="Can not be empty"
                        id="Name"
                        onFocus
                        value = {Name}
                        onChange={(e) => setName(e.target.value)}
                    />
                    <Input 
                        name="Email"
                        type="email"
                        required
                        title="Can not be empty"
                        id="Email"
                        value = {Email}
                        onChange={(e) => setEmail(e.target.value)}
                    />
                    <Input 
                        name="Birthday"
                        type="date"
                        required
                        title="Can not be empty"
                        id="Birthday"
                        value = {Birthday}
                        onChange={(e) => setBirthday(e.target.value)}
                    />
                    <Input 
                        name="From"
                        type="text"
                        required
                        title="Can not be empty"
                        id="From"
                        value = {From}
                        onChange={(e) => setFrom(e.target.value)}
                    />
                    <Input 
                        name="Password"
                        type="password"
                        required
                        title="Can not be empty"
                        id="Password"
                        value = {Password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                    <Input 
                        name="Majors"
                        type="text"
                        required
                        title="Can not be empty"
                        id="Majors"
                        value = {Majors}
                        onChange={(e) => setMajors(e.target.value)}
                    />
                    <Input 
                        name="Maxim"
                        type="text"
                        required
                        title="Can not be empty"
                        id="Maxim"
                        value = {Maxim}
                        onChange={(e) => setMaxim(e.target.value)}
                    />
                    <Input 
                        name="Describe"
                        type="text"
                        required
                        title="Can not be empty"
                        id="Describe"
                        value = {Describe}
                        onChange={(e) => setDescribe(e.target.value)}
                    />
    
                </article>
                <article className={cx("itemRight")}>
                    <Product value = {Job} name = "Job" type="text" onDataUpdate={handleJob}/>
                    <Product value = {Language} name = "Language" type="text" onDataUpdate={handleLanguage}/>
                    <Product value = {Phone} name = "Phone" type="number" onDataUpdate={handlePhone}/>
                    <Input 
                        name="CV"
                        type="file"
                        required
                        title="Can not be empty"
                        id="CV"
                        onChange={(e) => setCV(e.target.files[0])}
                    />
                    <Input 
                        name="Images"
                        type="file"
                        required
                        title="Can not be empty"
                        id="Images"
                        multiple
                        onChange={(e) => setImages(e.target.files[0])}
                    />
                   <Input 
                        name="Logo"
                        type="file"
                        required
                        title="Can not be empty"
                        id="Logo"
                        onChange={(e) => setLogo(e.target.files[0])}
                    />      
                   <Input 
                        name="IconLogo"
                        type="file"
                        required
                        title="Can not be empty"
                        id="IconLogo"
                        onChange={(e) => setIconLogo(e.target.files[0])}
                    />   
                    <Button
                        name="Create Account"
                        width = "30%"
                        height = "7%"
                        onClick={handleUpAccount}
                    />                       
                </article>
</form>
            {error && (
                <Notification
                    key={notificationKey}
                    content={error}
                    title="Warning"
                    type="warning"
                />
            )}
            {alert && (
                <Notification
                    content={alert}
                    title="Message"
                />
            )}
        </section>
     );
}

export default Account;
