import { useEffect } from "react";
import { toast } from "react-toastify";
import { useNavigate } from 'react-router-dom';

const Notification = ({ content, statuss, next = null }) => {
    const navigate = useNavigate();

    useEffect(() => {
        toast[statuss](content);
        if (next !== null) {
            setTimeout(() => {
                navigate(next);
            }, 1000);
        }
    }, [content, statuss, next, navigate]);

    return null; // Notification không render bất kỳ gì
};

export default Notification;
