  const props = {
    onClick
  }
  if(disabled) {
    delete props.onClick;
  }
  if(to) {
    props.to = to
    Component = Link
  }else if(href) {
    props.href = to
    Component = "a"
  }
  return (
    <Component {...props} className={cx("button")}>
      {leftIcon && <span className= {cx('icon')}> {leftIcon} </span>}
      <span className={cx('title')}>{name}</span>
      {leftIcon && <span className= {cx('icon')}>{}</span>}
    </Component>
  );
}

