import classNames from "classnames/bind";
import { useState } from "react";

import styles from "./CreateAccount.module.scss";
import Input from "../../../components/Input";
import Button from  "../../../components/Button";
import Product from "./MoreProduct";

const cx =classNames.bind(styles);

function Account() {

    const [name, setName] = useState(null);
    const [email, setEmail] = useState(null);
    const [birthday, setBirthday] = useState(null);
    const [from, setFrom] = useState(null);
    const [password, setPassword] = useState(null);
    const [majors, setMajors] = useState(null);
    const [maxim, setMaxim] = useState(null);
    const [describe, setDescribe] = useState(null);
    const [CV, setCV] = useState(null);
    const [images, setImages] = useState(null);
    const [logo, setLogo] = useState(null);
    const [IconLogo, setIconLogo] = useState(null);
    const [job, setJob] = useState(null);
    const [phone, setPhone] = useState(null);
    const [language, setLanguage] = useState(null);

    const handleJob = (data) => {
        setJob(data);
    };
    const handlePhone = (data) => {
        setPhone(data);
    };
    const handleLanguage = (data) => {
        setLanguage(data);
    };
    // console.log(job)
    return ( 
        <section className={cx("Container")}>
            <article className={cx("itemLeft")}>
                <Input 
                    name="Name"
                    type="text"
                    required
                    title="Can not be empty"
                    id="Name"
                />
                <Input 
                    name="Email"
                    type="email"
                    required
                    title="Can not be empty"
                    id="Email"
                />
                <Input 
                    name="Birthday"
                    type="date"
                    required
                    title="Can not be empty"
                    id="Birthday"
                />
                <Input 
                    name="From"
                    type="text"
                    required
                    title="Can not be empty"
                    id="From"
                />
                <Input 
                    name="Password"
                    type="password"
                    required
                    title="Can not be empty"
                    id="Password"
                />
                <Input 
                    name="Majors"
                    type="text"
                    required
                    title="Can not be empty"
                    id="Majors"
                />
                <Input 
                    name="Maxim"
                    type="text"
                    required
                    title="Can not be empty"
                    id="Maxim"
                />
                <Input 
                    name="Describe"
                    type="text"
                    required
                    title="Can not be empty"
                    id="Describe"
                />

            </article>
            <article className={cx("itemRight")}>
                <Product name = "Job" onDataUpdate={handleJob}/>
                <Product name = "Language" onDataUpdate={handleLanguage}/>
                <Product name = "Phone" onDataUpdate={handlePhone}/>
                <Input 
                    name="CV"
                    type="file"
                    required
                    title="Can not be empty"
                    id="CV"
                />
                <Input 
                    name="Images"
                    type="file"
                    required
                    title="Can not be empty"
                    id="Images"
                    multiple
                />
               <Input 
                    name="Logo"
                    type="file"
                    required
                    title="Can not be empty"
                    id="Logo"
                />      
               <Input 
                    name="IconLogo"
                    type="file"
                    required
                    title="Can not be empty"
                    id="IconLogo"
                />   
                <Button
                    name="Create Account"
                    width = "30%"
                    height = "7%"
                />                       
            </article>
        </section>
     );
}

export default Account;
