import classNames from "classnames/bind";
import { useState, useEffect } from "react";

import styles from "./Home.module.scss";
import Input from "../../../components/Input";
import Button from "../../../components/Button";
// import Notification from "../../../components/Notification";
import { getMessage } from "../../../Services/sendMessage";

const cx = classNames.bind(styles);

function Home() {
    const [data, setData] = useState([]);
    const [displayedItemId, setDisplayedItemId] = useState(null);

    useEffect(() => {
        // Call the API when the component mounts
        fetchAPI();
    }, []);

    const handleDisplay = (itemId) => {
        // Toggle the display of the clicked item
        setDisplayedItemId((prevId) => (prevId === itemId ? null : itemId));
    };

    // Call API
    const fetchAPI = async () => {
        try {
            const result = await getMessage();
            setData(result.dataMessage);
        } catch (error) {
            console.log(error);
            // Handle error
        }
    };

    return (
        <section className={cx("listMessage")}>
            {data.map((item) => (
                <section className={cx("message")} key={item._id}>
                    <article
                        className={cx("title")}
                        onClick={() => handleDisplay(item._id)}
                    >
                        <p className={cx("nameTitle")}>Khanh Dinh Van</p>
                        <h1 className={cx("titleMessage")}>{item.TitleMessage}</h1>
                        <i className={cx("bi bi-trash3", "remote")}></i>
                    </article>
                    <section
                        className={cx("container", {
                            "container-displayed": displayedItemId === item._id,
                        })}
                    >
                        <article className={cx("itemTilteMessage")}>
                            <h1>{item.TitleMessage}</h1>
                        </article>
                        <article className={cx("itemAccountReceiver")}>
                            <span>{item.NameUserReceiver}</span>
                            <p>{item.EmailReceiver}</p>
                        </article>
                        <article className={cx("itemContentMessage")}>
                            <p>{item.Content}</p>
                        </article>
                        <form className={cx("itemReplyMessage")}>
                            <Input
                                name="Reply The Message"
                                type="text"
                                required
                                id={`itemReplyMessage-${item._id}`}
                                onFocus={() => {}}
                            />
                            <Button
                                name="Send Message"
                                width="15%"
                                height="10%"
                                padding="1%"
                            />
                        </form>
                    </section>
                </section>
            ))}
        </section>
    );
}

export default Home;
