import classNames from "classnames/bind";
import styles from "./Menu.module.scss";
import {Link} from "react-router-dom";

const cx = classNames.bind(styles);

function MenuItem({ data, title }) {

  return (
    <section className = {cx("menu-item")} >
      <article className = {cx("titleMenu")} > {title} 
        <i className={cx("bi bi-chevron-down", "icontTitle")}></i>
      </article>
      <section className = {cx("contentMenu")}>
        <article className={cx("itemContent")} >

        </article>
        {data.map((item, index) => (
          <Link to ={item.to} className = {cx("value")}></Link> 
        ))}
      </section>
    </section>
    // <select className={cx("")} onChange={handleChange}>
    //   <option disabled selected hidden className = {cx("value")}>{title}</option>
    //   {data.map((item, index) => (
    //     <option   key={index} value=>{item.value}</option>
    //   ))}
    // </select>
  );    
}

export default MenuItem;
