import classNames from "classnames/bind";
import {useRef} from "react";

import style from "./UploadImage.module.scss";

const cx = classNames.bind(style);

function UploadImage({data, name, id, required = false, multiple = false}) {

    const fileInputRef = useRef(null);

    const handleButtonClick = () => {
        fileInputRef.current.click();
    }

    return ( 
        <section className={cx("wrapper")}>
            <section className={cx("content")}>
                <img src="https://i.pinimg.com/564x/d5/cb/26/d5cb26a3c7b95fdb2ae7a330981d3873.jpg" alt="s"/>
                {/* value={data} */}
                <article className={cx("item")}>
                    <button>
                        Chọn {name} Của Bạn
                        <input type="file" name={name} id={id} required = {required} multiple = {multiple} />
                    </button>
                </article>
            </section>
        </section>
     );
}

export default UploadImage;
