import className from "classnames/bind";
import { useState } from "react";
import axios from "axios";

import styles from "./Login.module.scss";
import { images } from "../../asset/img";
import Input from "../../components/components/Input";
import Button from "../../components/components/Button";
import Notification from "../../components/components/Notification"

const cx = className.bind(styles);

function Login() {

    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const handleLogin = async (e) => {
        e.preventDefault(); // Prevents the default form submission behavior
        try {
            const response = await axios.post("http://localhost:7000/api/account/signin", {
                email: email,
                password: password
            });
            localStorage.setItem("accsessToken", response.data.accsessToken);
            window.location.replace("/admin");
            
        } catch (error) {
            // thông báo lỗi
            console.log(error.response.data.message);
            <Notification
                content= {error.response.data.message}
                title= "Message"
                type= "error"

            />
        }
    };


    return ( 
        <main className = {cx("loginMain")}>
            <section className={cx("container")}>
                <article className = {cx("images")}>
                    <img src={images.Lovlyze} alt="lovlyze"/>
                </article>
                <section  className={cx("content")}>
                    <form action="/login" method="post" >
                        <Input 
                            name="Gmail"
                            type="email"
                            required
                            title= "gmail cannot be empty"
                            id="email"
                            onChange={(e) => setEmail(e.target.value)}
                        />
                        <Input 
                            name="Password"
                            type="password"
                            required
                            title= "password cannot be empty"
                            id="password"
                            onChange={(e) => setPassword(e.target.value)}
                        />
                        <Button
                            name="Login"
                            type= "submit"
                            onClick={handleLogin}
                            width = "30%"
                            height= "15%"
                            margin= "4% 35%"
                        />
                    </form>
                 </section>
            </section>
        </main>
     );
}

export default Login;
