import classNames from "classnames/bind";
import styles from "./List.module.scss";
import Input from "../../../../components/Input";
import MoreSkills from "../Create/MoreSkills";
import { useEffect, useState } from "react";
import { deleteSkill, getSkill } from "../../../../Services/skills";
import { toast } from "react-toastify";
import Button from "../../../../components/Button";
import Update from "../Update";

const cx = classNames.bind(styles);

function ListSkills() {
    
    const [data, setData] = useState([]);
    const [Skills, SetSkills] = useState([]);
    const [TitleSkills, SetTitleSkills] = useState("");
    const [ContentSkills, SetContentSkills] = useState("");

    const getAllSkills = async () => {
        try {
            const result = await getSkill();
            setData(result.data);
        } catch (error) {
            toast.error("Can not data")
        }
    }

    useEffect(() => {
        getAllSkills()
    }, []);

    const handleDelete = async (id) => {
        try {
            const resutlt = await deleteSkill(id);
            toast.success(resutlt.message);
            getAllSkills();
        } catch (error) {
            console.log(error);
        }
    } 
    const handleUpdate = async (id) => {
        console.log("ContentSkills: ", ContentSkills);
        console.log("TitleSkills: ", TitleSkills);
        console.log("Skills: ", Skills);
    } 

    return (
        <section className={cx("warpper")}>
        {
            data && data.map((item, key) => (
                <Update 
                    item
                />
            ))
        }
        </section>
    )
}
 export default ListSkills;
