import classNames from "classnames/bind";

import styles from "./List.module.scss";
import {  useState } from "react";
import { getResult, deleteResult } from "../../../../Services/result";
import { toast } from "react-toastify";

const cx = classNames.bind(styles)

function ListResult() {

    const [data, SetData] = useState([]);
    const [update, SetUpdate] = useState(-1);

    const getAPI = async () => {
        try {
            const resultData = await getResult()
            SetData(resultData.data);
        } catch (error) {
            toast.error("The system is maintenance")
            console.log("error: ".error);
        }
    }

    getAPI();

    const handleDelete = async (id) => {
        try {
            const resultDelete = await deleteResult(id);
            toast.success(resultDelete.message);  
        } catch (error) {
          console.log(error);  
          toast.error("The system is maintenance")
        }
    }
    const handleUpdate = async (id) => {
        SetUpdate(id);
    }

    const Edit = () => {
        return(
            <tr>
                <td>
                    <input value="name"/>
                </td>
                <td> <input value="Degree" /></td>
                <td> <input value="Date" /></td>
                <td> <input value="SchoolName" /> </td>
                <td> <input value="Describe" /></td>
                <td>
                    <button className={cx("Update")}>Edit</button>
                </td>
            </tr>            
        )
    }

    return (
        <section className={cx("warpper")}>
            <table className={cx("table")}>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Degree</th>
                        <th>Date</th>
                        <th>School Name</th>
                        <th>Describe</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        data.map((item, index) => (
                            update === item._id ? <Edit/> :
                            <tr key={index}>
                                <td>{item.Name}</td>
                                <td>{item.Degree}</td>
                                <td>{item.Date}</td>
                                <td>{item.School} Name</td>
                                <td>{item.Describe}</td>
                                <td>
                                    <button onClick={() => {handleDelete(item._id)}} className={cx("Delete")}>Delete</button>
                                    <button onClick={() => {handleUpdate(item._id)}} className={cx("Edit")}>Edit</button>
                                </td>
                            </tr>
                        ))
                    }
                </tbody>
                
            </table>
        </section>
    )
}

export default ListResult;
