import React, { useState } from 'react';
import classNames from 'classnames/bind';

import styles from "./ItempProject.module.scss";

const cx = classNames.bind(styles);

function ItemProject({ item, status, onToggle }) {
    const [statuss, setStatus] = useState(status);

    const handleClick = () => {
        setStatus(!statuss); // Khi click, đảo ngược trạng thái của statuss
        onToggle(); // Gọi hàm onToggle để thông báo cho component cha
    };

    return (
        <section className={cx("warpper")}>
            <section className={cx("desProject", { 'visible': statuss })}>
                <article className={cx("imageDesProject")}>
                    <img src={item.Image[1]} alt="imgProject" />
                </article>
                <section className={cx("contentDesProject")}>
                    <section className={cx("itemContentDesProject")}>
                        <h2>Introduce</h2>
                        <p>{item.Introduce}</p>
                        <h2>Technology</h2>
                        <ul>
                            {item.Technology.map((itemTechnology, indexTechnology) => {
                                return (
                                    <li key={indexTechnology}>{itemTechnology}</li>
                                )
                            })}
                        </ul>
                        <h2>Objects included in the project</h2>
                        <ul>
                            {item.ObjectInProject.map((itemObjectInProject, indexObjectInProject) => {
                                return (
                                    <li key={indexObjectInProject}>{itemObjectInProject}</li>
                                )
                            })}
                        </ul>
                        <a href={`${item.LinkProject}`}>
                            <button>Experience now</button>
                        </a>
                    </section>
                    <article className={cx("icontDesProject")}>
                        <i onClick={handleClick} className="bi bi-x-circle"></i>
                    </article>
                </section>
            </section>
        </section>
    )
}

export default ItemProject;
