import classNames from "classnames/bind";


import styles from "./Menu.module.scss";

const cx = classNames.bind(styles);

function MenuItem({data, title}) {

    const handleChange = (event) => {
        const selectedIndex = event.target.selectedIndex;
        const selectedOption = event.target.options[selectedIndex];
        window.location.href = selectedOption.getAttribute('href');
    };

    return ( 
        <select className={cx("menu-item")} onChange={handleChange}>
            <option>{title}</option>
            {

                data.map((item, index) => (
                    <option value="/admin1"> key={index}> 
                        <i className = {cx(item.icont, "icont")}></i>
                        <span className = {cx("value")}> {item.value}</span>
                    </option>
                ))
            }
        </select>
     );
}

export default MenuItem;
