import React from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom"; // Thêm "Routes" và "Route" vào imports

import { pulicRouter, privateRouters } from "./routers";
import { DefaultLayout, DefaultLayoutAdmin } from "./layouts";

function App() {
  const token = localStorage.getItem("accessToken");
  return (
    <Router>
      <div className="App">
        <Routes>
          {token ? 
          ( // Chuyển pulicRouter.map thành toán tử điều kiện
            pulicRouter.map((route, index) => {
              let Layout = DefaultLayout;
              if (route.Layout) {
                Layout = route.Layout;
              } else if (route.Layout === null) {
                Layout = React.Fragment; // Thay Fragment thành React.Fragment
              }

              const Page = route.component;
              return (
                <Route
                  key={index}
                  path={route.path}
                  element={<Layout> <Page/> </Layout>} // Thêm dấu nháy kép cho Layout và Page
                />
              );
            })
          ) : (
            privateRouters.map((route, index) => {
              let Layout = DefaultLayoutAdmin;
              if (route.Layout) {
                Layout = route.Layout;
              } else if (route.Layout === null) {
                Layout = React.Fragment; // Thay Fragment thành React.Fragment
              }

              const Page = route.component;
              return (
                <Route
                  key={index}
                  path={route.path}
                  element={<Layout> <Page/> </Layout>} // Thêm dấu nháy kép cho Layout và Page
                />
              );
            })
          )}
        </Routes>
      </div>
    </Router>
  );
}

export default App;
